// G-005: Fuzzy JS Matching Before AI
// Patterns from patterns/force-matching.js - run JS before AI calls

const FORCE_PATTERNS = {
  'metropolitan police': 'Metropolitan Police Service',
  'met police': 'Metropolitan Police Service',
  'greater manchester police': 'Greater Manchester Police',
  'west midlands police': 'West Midlands Police',
  'west yorkshire police': 'West Yorkshire Police',
  'city of london police': 'City of London Police',
  'kent police': 'Kent Police',
  'essex police': 'Essex Police',
  'surrey police': 'Surrey Police',
  'sussex police': 'Sussex Police',
  'thames valley police': 'Thames Valley Police',
  'hampshire constabulary': 'Hampshire Constabulary',
  'hertfordshire constabulary': 'Hertfordshire Constabulary',
  'bedfordshire police': 'Bedfordshire Police',
  'avon and somerset': 'Avon and Somerset Police',
  'devon and cornwall': 'Devon & Cornwall Police',
  'dorset police': 'Dorset Police',
  'gloucestershire constabulary': 'Gloucestershire Constabulary',
  'wiltshire police': 'Wiltshire Police',
  'merseyside police': 'Merseyside Police',
  'lancashire constabulary': 'Lancashire Constabulary',
  'cheshire constabulary': 'Cheshire Constabulary',
  'cumbria constabulary': 'Cumbria Constabulary',
  'south yorkshire police': 'South Yorkshire Police',
  'north yorkshire police': 'North Yorkshire Police',
  'humberside police': 'Humberside Police',
  'northumbria police': 'Northumbria Police',
  'cleveland police': 'Cleveland Police',
  'durham constabulary': 'Durham Constabulary',
  'nottinghamshire police': 'Nottinghamshire Police',
  'leicestershire police': 'Leicestershire Police',
  'lincolnshire police': 'Lincolnshire Police',
  'northamptonshire police': 'Northamptonshire Police',
  'derbyshire constabulary': 'Derbyshire Constabulary',
  'warwickshire police': 'Warwickshire Police',
  'west mercia police': 'West Mercia Police',
  'staffordshire police': 'Staffordshire Police',
  'norfolk constabulary': 'Norfolk Constabulary',
  'suffolk constabulary': 'Suffolk Constabulary',
  'cambridgeshire constabulary': 'Cambridgeshire Constabulary',
  'dyfed-powys police': 'Dyfed-Powys Police',
  'gwent police': 'Gwent Police',
  'north wales police': 'North Wales Police',
  'south wales police': 'South Wales Police',
  'police scotland': 'Police Scotland',
  'british transport police': 'British Transport Police',
  'counter terrorism policing': 'Counter Terrorism Policing',
  'national crime agency': 'National Crime Agency',
  'ministry of defence police': 'Ministry of Defence Police',
  'civil nuclear constabulary': 'Civil Nuclear Constabulary'
};

function lookupForce(text) {
  if (!text) return null;
  const textLower = text.toLowerCase();
  for (const [pattern, forceName] of Object.entries(FORCE_PATTERNS)) {
    if (textLower.includes(pattern)) {
      return forceName;
    }
  }
  return null;
}

// Build force name to record ID lookup from forces data
const forces = [];
const seenForceIds = new Set();
for (const item of $input.all()) {
  const forceName = item.json.name || item.json.Name;
  if (forceName && item.json.id && !seenForceIds.has(item.json.id)) {
    if (!item.json.raw_data && !item.json.title) {
      forces.push({ name: forceName, recordId: item.json.id });
      seenForceIds.add(item.json.id);
    }
  }
}

const forceLookup = {};
for (const force of forces) {
  forceLookup[force.name] = force.recordId;
}

// Get unique signals
const signalsMap = new Map();
for (const item of $input.all()) {
  if (item.json.title && item.json.raw_data && !signalsMap.has(item.json.id)) {
    signalsMap.set(item.json.id, item.json);
  }
}

const results = [];
for (const signal of signalsMap.values()) {
  let rawData = {};
  try {
    rawData = JSON.parse(signal.raw_data);
  } catch (e) {
    rawData = {};
  }

  const company = rawData.company_name || '';
  const location = rawData.location || '';
  const title = signal.title || rawData.job_title || '';
  const description = rawData.description_text || rawData.description || '';

  const matchedForceName = lookupForce(company) || lookupForce(location) || lookupForce(title);
  const matchedForceId = matchedForceName ? forceLookup[matchedForceName] : null;

  results.push({
    json: {
      signal_id: signal.id,
      signal_title: title,
      company_name: company,
      location: location,
      description_text: description.substring(0, 2000),
      existing_force: signal.force || null,
      pattern_matched_force: matchedForceName,
      pattern_matched_force_id: matchedForceId,
      pattern_confidence: matchedForceId ? 90 : 0,
      force_lookup: forceLookup
    }
  });
}

return results;

// Derive status from AI response and pattern matching
// Per SPEC-003: score >= 70 = relevant, < 70 = irrelevant
// Force: use pattern match if confidence >= 80, else use AI force if confidence >= 80

const aiResponseRaw = $input.item.json;

// Parse the AI response - handle nested Langchain OpenAI output structure
let aiResponse = {};
try {
  let responseText = '';

  if (aiResponseRaw.output && Array.isArray(aiResponseRaw.output)) {
    const firstOutput = aiResponseRaw.output[0];
    if (firstOutput && firstOutput.content && Array.isArray(firstOutput.content)) {
      responseText = firstOutput.content[0]?.text || '';
    }
  } else if (typeof aiResponseRaw.content === 'string') {
    responseText = aiResponseRaw.content;
  } else if (typeof aiResponseRaw.message === 'string') {
    responseText = aiResponseRaw.message;
  } else if (typeof aiResponseRaw.text === 'string') {
    responseText = aiResponseRaw.text;
  } else {
    responseText = JSON.stringify(aiResponseRaw);
  }

  const jsonMatch = responseText.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    aiResponse = JSON.parse(jsonMatch[0]);
  }
} catch (e) {
  aiResponse = {
    relevant: false,
    confidence: 0,
    reasoning: 'Failed to parse AI response: ' + e.message,
    force: null,
    force_confidence: 0,
    rejection_reason: 'AI parsing error',
    role_type: 'Unknown'
  };
}

// CRITICAL: Get signal_id from AI response (echo-back pattern)
const signalId = aiResponse.signal_id;

if (!signalId) {
  let fallbackSignalId = null;
  try {
    const loopItem = $('Loop: Process Each').item;
    if (loopItem?.json?.signal_id) {
      fallbackSignalId = loopItem.json.signal_id;
    }
  } catch (e1) {}

  if (!fallbackSignalId) {
    try {
      const patternItem = $('Code: Force Pattern Match').item;
      if (patternItem?.json?.signal_id) {
        fallbackSignalId = patternItem.json.signal_id;
      }
    } catch (e2) {}
  }

  if (fallbackSignalId) {
    aiResponse.signal_id = fallbackSignalId;
  } else {
    throw new Error(`Could not determine signal_id. AI response did not include it.`);
  }
}

// Get original signal data for pattern matching info
let signal = {};
try {
  const loopItem = $('Loop: Process Each').item;
  if (loopItem?.json) signal = loopItem.json;
} catch (e) {
  try {
    const patternItem = $('Code: Force Pattern Match').item;
    if (patternItem?.json) signal = patternItem.json;
  } catch (e2) {
    signal = {};
  }
}

const confidence = typeof aiResponse.confidence === 'number' ? aiResponse.confidence : 0;
const status = confidence >= 70 ? 'relevant' : 'irrelevant';

let forceIdToLink = null;
let forceSource = null;

let forceLookup = {};
try {
  if (typeof signal.force_lookup === 'string') {
    forceLookup = JSON.parse(signal.force_lookup);
  } else if (signal.force_lookup && typeof signal.force_lookup === 'object') {
    forceLookup = signal.force_lookup;
  }
} catch (e) {
  forceLookup = {};
}

const patternConfidence = signal.pattern_confidence || 0;
if (patternConfidence >= 80 && signal.pattern_matched_force_id) {
  forceIdToLink = signal.pattern_matched_force_id;
  forceSource = 'pattern';
} else if (aiResponse.force_confidence >= 80 && aiResponse.force) {
  const aiForceId = forceLookup[aiResponse.force];
  if (aiForceId) {
    forceIdToLink = aiForceId;
    forceSource = 'ai';
  }
} else if (signal.existing_force && Array.isArray(signal.existing_force) && signal.existing_force.length > 0) {
  forceIdToLink = signal.existing_force[0];
  forceSource = 'existing';
}

return {
  json: {
    signal_id: aiResponse.signal_id,
    signal_title: signal.signal_title || aiResponse.signal_title || 'unknown',
    relevance_score: confidence,
    relevance_reason: aiResponse.reasoning || 'No reasoning provided',
    status: status,
    force_id: forceIdToLink,
    force_source: forceSource,
    ai_force: aiResponse.force,
    ai_force_confidence: aiResponse.force_confidence,
    pattern_force: signal.pattern_matched_force,
    pattern_force_id: signal.pattern_matched_force_id,
    role_type: aiResponse.role_type || 'Unknown',
    rejection_reason: aiResponse.rejection_reason
  }
};
