if (payload.body && payload.body.body && payload.body.body.body && Array.isArray(payload.body.body.body)) {
  jobs = payload.body.body.body;
} else if (payload.body && payload.body.body && Array.isArray(payload.body.body)) {
  jobs = payload.body.body;
} else if (payload.body && Array.isArray(payload.body)) {
  jobs = payload.body;
} else if (Array.isArray(payload)) {
  jobs = payload;
} else if (payload.data && Array.isArray(payload.data)) {
  jobs = payload.data;
} else if (payload.results && Array.isArray(payload.results)) {
  jobs = payload.results;
} else {
  const keys = Object.keys(payload);
  for (const key of keys) {
    if (Array.isArray(payload[key])) {
      jobs = payload[key];
      break;
    }
  }
}

console.log(`Parsed ${jobs.length} jobs from Bright Data webhook`);

if (jobs.length === 0) {
  return [{ json: { _no_jobs: true, _payload_received: JSON.stringify(payload).substring(0, 500) } }];
}

return jobs.map(job => ({ json: { ...job, received_at: new Date().toISOString() } }));

