function lookupForce(text) {
  if (!text) return null;
  const t = text.toLowerCase();
  for (const [p, f] of Object.entries(FORCE_PATTERNS)) {
    if (t.includes(p)) return f;
  }
  return null;
}

const allItems = $input.all();
const forces = [];
const jobs = [];

for (const item of allItems) {
  if (item.json.id && item.json.id.startsWith('rec') && item.json.name) {
    forces.push(item);
  } else {
    jobs.push(item);
  }
}

const forceIdLookup = {};
for (const f of forces) {
  forceIdLookup[f.json.name.toLowerCase()] = f.json.id;
}

const results = [];
for (const item of jobs) {
  const company = item.json.company_name || item.json.company || '';
  const location = item.json.location || '';
  const title = item.json.title || item.json.job_title || '';

  const matchedForce = lookupForce(company) || lookupForce(location) || lookupForce(title);
  const forceRecordId = matchedForce ? forceIdLookup[matchedForce.toLowerCase()] : null;

  results.push({
    json: {
      ...item.json,
      matched_force: matchedForce,
      force_record_id: forceRecordId,
      force_match_confidence: matchedForce ? 0.9 : 0
    }
  });
}

return results;

function simpleHash(str) {
  let h = 0;
  for (let i = 0; i < str.length; i++) {
    h = ((h << 5) - h) + str.charCodeAt(i);
    h = h & h;
  }
  return Math.abs(h).toString(36);
}

const results = [];
for (const item of $input.all()) {
  const url = item.json.url || item.json.link || '';
  const external_id = url
    ? `indeed_${simpleHash(url)}`
    : `indeed_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

  results.push({
    json: {
      ...item.json,
      external_id,
      source: 'indeed',
      type: 'job_posting',
      status: 'new',
      detected_at: new Date().toISOString()
    }
  });
}

return results;

const jobs = $('Code: Generate External IDs').all();
const airtableResults = $input.all();
const existingIds = new Set();

for (const result of airtableResults) {
  if (result.json.external_id) {
    existingIds.add(result.json.external_id);
  }
  if (result.json.raw_data) {
    try {
      const parsed = JSON.parse(result.json.raw_data);
      if (parsed.external_id) {
        existingIds.add(parsed.external_id);
      }
    } catch (e) {}
  }
}

const newJobs = jobs.filter(job => !existingIds.has(job.json.external_id));
const skipped = jobs.length - newJobs.length;

console.log(`Dedupe: ${jobs.length} total, ${newJobs.length} new, ${skipped} skipped`);

return newJobs;
